scriptTask.script:
	//设置成本中心审批人
def costCenterDrools = new groovy.json.JsonSlurper().parseText(execution.getVariable("_http_response_body"));
def localcostCenterDrools = costCenterDrools.find { elem -> elem.costcenter == new groovy.json.JsonSlurper().parseText(execution.getVariable('costcenter_t00')).code}
def localVerifiers =[];
//设置一级二级三级审批人
localVerifiers << localcostCenterDrools.verifierfirst;
localVerifiers << localcostCenterDrools.verifiersecond;
localVerifiers << localcostCenterDrools.verifierthird;
//当一级审批人为报销人时，审批人替换成reverifierfirst，reverifierfirst配置成a,b,....  a为一级审批人，b为二级审批人,目前最多支持三级
if(localVerifiers[0] == initiator){
	try{
		if(localcostCenterDrools.reverifierfirst)
			localVerifiers = localcostCenterDrools.reverifierfirst.split(",").toList();
	}catch(Exception e) {
		throw new org.activiti.engine.ActivitiIllegalArgumentException("成本中心审批人设置错误，请联系管理员");
	}
}
if(localVerifiers[1] == initiator){
	try{
		if(localcostCenterDrools.reverifiersecond)
			localVerifiers = localcostCenterDrools.reverifiersecond.split(",").toList();
	}catch(Exception e) {
		throw new org.activiti.engine.ActivitiIllegalArgumentException("成本中心审批人设置错误，请联系管理员");
	}
}
if(localVerifiers[2] == initiator){
	try{
		if(localcostCenterDrools.reverifierthird)
			localVerifiers = localcostCenterDrools.reverifierthird.split(",").toList();
	}catch(Exception e) {
		throw new org.activiti.engine.ActivitiIllegalArgumentException("成本中心审批人设置错误，请联系管理员");
	}
}
localVerifiers.removeAll([null, ""]);
localVerifiers.clear();
localVerifiers << initiator;
execution.setVariable("assigneeList",localVerifiers);
